import React, { useState, useContext } from "react";
import { fbChecklist } from "../../data";
import { NavLink } from "react-router-dom";
import Question from "../../components/Question";
import { Context } from "../../Context";
import { FaRegEdit } from "react-icons/fa";

function FbCategoryA() {
  const fbChecklistCategory = "A";
  const { score, increaseScore } = useContext(Context);

  const filteredQuestions = fbChecklist.filter(
    (check) => check.category === fbChecklistCategory
  );

  const handleSubmit = () => {
    //obtain array of passed/checked questions
    const checkedQuestions = fbChecklist.filter((question) => {
      return question.checked === true;
    });
    //iterate through passed/checked questions and increase the score
    for (let i = 0; i < checkedQuestions.length; i++) {
      increaseScore();
    }
  };
  return (
    <div className="category-head">
      <h2>FB Checklist Category A</h2>
      {filteredQuestions.map((question, index) => {
        const { id, category, text, checked } = question;
        return (
          <Question id={id} category={category} text={text} checked={checked} />
        );
      })}
      <NavLink exact to="/fbCategoryB">
        <button>Previous</button>
      </NavLink>
      <NavLink exact to="/fbCategoryB">
        <button>Next</button>
      </NavLink>
      <button
        onClick={() => {
          handleSubmit();
        }}
      >
        Submit
      </button>
      <div>{score}</div>
    </div>
  );
}

export default FbCategoryA;
