  //   sethomeaudits(newAudits);
  // };
  const filterAudits = () => {};
  //filter audits based on selected category
  // const filterAudits = (category) => {
  //   if (category === "all") {
  //     sethomeaudits(audits);
  //     return;
  //   }
  // };

  return (
    <AppProvider
      value={{
        homeaudits,
        homeCategories,
        filterAudits,
      }}
