import React, { useState, useContext, useEffect } from "react";
import { audits } from "./data";

const AppContext = React.createContext();

const AppProvider = ({ children }) => {
  //set state of audits appearing on home page
  const [homeaudits, sethomeaudits] = useState(audits);
  //set state of categories which filter audits displayed
  const [homeCategories, sethomeCategories] = useState([]);
  //set new audits
  const setNewHomeAudits = (newAudits) => {
    sethomeaudits(newAudits);
  };
  //filter audits based on selected category
  const filterAudits = (category) => {
    if (category === "all") {
      sethomeaudits(audits);
      return;
    }
  };

  return (
    <AppProvider
      value={{
        homeaudits,
        homeCategories,
        filterAudits,
      }}
    >
      {children}
    </AppProvider>
  );
};

export const useGlobalContext = () => {
  return useContext(AppContext);
};

export { AppContext, AppProvider };
