import React, { useState, createContext } from "react";

export const Context = createContext();

export const ContextProvider = (props) => {
  //state to keep track of tenant score
  const [score, setScore] = useState(0);
  //state to check if modal is open
  const [modalOpen, setModalOpen] = useState(false);
  //function to increase score state
  const increaseScore = () => {
    setScore((prevScore) => {
      return prevScore + 1;
    });
  };
  //functions to change modalOpen state
  const openModal = () => {
    setModalOpen(true);
  };
  const closeModal = () => {
    setModalOpen(false);
  };

  return (
    <Context.Provider value={{ score, setScore, increaseScore }}>
      {props.children}
    </Context.Provider>
  );
};
