import React, { useState, useEffect, useContext } from "react";
import { Typography, Button, Input, TextField, FormGroup, FormControlLabel } from "@material-ui/core";
import axios from "axios";

import { Context } from "../Context";
import Navbar from "../Navbar";
import useStyles from "../styles";
import { Container } from "@material-ui/core";

function EditPassword() {
  const {
    accountState,
    getAccountInfo
  } = useContext(Context);

  // All possible account info categories for 3 types of users
  const {
    acc_id,
    branch_id,
    employee_id,
    email,
    username,
    first_name,
    last_name,
    hp,
    mgr_id,
    role_id,
    store_id,
    type_id,
  } = accountState;

  const API_URL = "http://localhost:8080";
  const [passwordState, setPasswordState] = useState("");
  const [confirmPasswordState, setConfirmPasswordState] = useState("");
  const styles = useStyles();

  function postPasswordChange(newPassword) {
    console.log("This is posting password change");
    let FormData = require("form-data");
    let formdata = new FormData();
    formdata.append("new_password", newPassword);
    return axios
      .post(
        `${API_URL}/account/postPasswordUpdate`,
        formdata,
        {
          headers: {
            "Content-Type": `multipart/form-data; boundary=${formdata._boundary}`,
          },
        }
      )
      .then((response) => {
        console.log(response);
      })
      .catch(() => {
        console.log("Failed password change submission");
      })
  }

  return (
    <main className={styles.main}>
      <Typography variant="h3" align="center">Edit Password</Typography>
      <br />
      <FormGroup column="true">
        <TextField label="New Password" onChange={(e) => setPasswordState(e.target.value)}/>
        <TextField label="Confirm New Password" onChange={(e) => setConfirmPasswordState(e.target.value)}/>
      </FormGroup>
      <Button 
        align="center"
        variant="outlined"
        color="primary"
        className={styles.buttons}
        onClick={() => console.log(passwordState)}
      >
        Submit
      </Button>
    </main>
  )
}

export default EditPassword;
