import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";

const useStyles = makeStyles((theme) => ({
  commentBox: {
    maxWidth: 800,
    width: "100%",
    padding: theme.spacing(0, 0, 2, 2),
  },
  comment: {
    width: "100%",
  },
  //   responseContainer: {
  //     padding: theme.spacing(0, 0, 0, 2),
  //   },
}));

function TenantReportCard() {
  const classes = useStyles();
  const [comment, setComment] = useState("");

  //function to update comment state
  const handleComment = (e) => {
    setComment(e.target.value);
  };
  return (
    <Accordion>
      <AccordionSummary
        expandIcon={<ExpandMoreIcon />}
        aria-label="Expand"
        aria-controls="additional-actions1-content"
        id="additional-actions1-header"
      >
        <ListItem>
          <ListItemText id={data.entry_id} primary={data.Requirement} />
        </ListItem>
      </AccordionSummary>
      <AccordionDetails className={classes.dropdownMain}>
        <div className={classes.dropdownContainer}>
          <Typography
            color="textSecondary"
            variant="button"
            className={classes.topText}
          >
            REMARKS: {data.remarks}
          </Typography>

          <Typography
            color="textSecondary"
            variant="button"
            className={classes.textInfo}
          >
            RECTIFICATION PERIOD: {timeframe}
          </Typography>
        </div>
        <div className={classes.tenantInstructions}>
          <Avatar src="/broken-image.jpg" className={classes.avatar} />
          <Typography color="textPrimary" className={classes.textTenant}>
            Please respond to non-compliance below:
          </Typography>
        </div>
        <div className={classes.commentBox}>
          <TextField
            id="standard-multiline-static"
            label="Comment on non-compliance"
            multiline
            value={comment}
            className={classes.comment}
            onChange={(e) => {
              handleComment(e);
            }}
          />
        </div>
        <input
          accept="image/*"
          className={classes.input}
          id="icon-button-file"
          type="file"
        />
        <label htmlFor="icon-button-file" className={classes.camera}>
          <IconButton
            color="primary"
            aria-label="upload picture"
            component="span"
          >
            <Typography variant="button" className={classes.uploadText}>
              Upload photo
            </Typography>
            <PhotoCamera />
          </IconButton>
        </label>
        <div className={classes.buttonContainer}>
          <Button
            className={classes.button}
            size="large"
            onClick={() => {
              handleSubmit();
            }}
            // color="secondary"
          >
            submit
          </Button>
        </div>
      </AccordionDetails>
    </Accordion>
  );
}

export default TenantReportCard;
