  root: {
    flexGrow: 1,
    textTransform: "none",
    color: "#000000",
    fontWeight: theme.typography.fontWeightBold,
    fontSize: theme.typography.pxToRem(16),
    margin: theme.spacing(2, 0, 2, 0),
    "&:focus": {
      opacity: 1,
    },
  },
}))((props) => <Tab disableRipple {...props} />);

