        //   setAuditsState(reportInfoArray);
        // }
      } catch (err) {
        console.log(err);
      }
    }
    getResponse();
  };

  return (
    <Paper className={classes.root}>
      <StyledTabs value={value} onChange={handleChange} centered>
        <StyledTab
          onClick={() => {
            filterAudits("UNRESOLVED");
          }}
          label="UNRESOLVED"
        />
        <StyledTab
          onClick={() => {
            filterAudits("RESOLVED");
          }}
          label="RESOLVED"
        />
        {/* <StyledTab
          onClick={() => {
            filterAudits("OVERDUE");
          }}
          label="OVERDUE"
        /> */}
      </StyledTabs>
    </Paper>
  );
}
