import React, { useState, useContext } from "react";
import Modal from "./Modal";
import { FaRegEdit } from "react-icons/fa";
import { Context } from "../Context";
import { Link } from "react-router-dom";

function Question({ id, text, tenantId }) {
  //Context: state of modal (whether it is open or not)
  const { setModalOpen, closeModal } = useContext(Context);
  //Context: Fb Checklist
  const { fbChecklistState } = useContext(Context);
  //Context: state of tenants to update checklist check
  const { tenantsState, updateFbChecklistChecked } = useContext(Context);
  //get the array containing this question object
  //to be used to update it's checked property in handleChange() function
  const thisQuestion = fbChecklistState.find((question) => {
    return question.id === id;
  });
  //function to handle change when checkbox is clicked
  const handleChange = () => {
    updateFbChecklistChecked(tenantId, id);
  };

  return (
    <div>
      <span>{text}</span>
      <input
        type="checkbox"
        id={id}
        name={`Check${id}`}
        onChange={() => {
          handleChange();
        }}
      />
      <FaRegEdit
        onClick={() => {
          setModalOpen(true);
        }}
      />
      <Modal id={id} />
      <Link to={`/tenant/${tenantId}`}>Submit</Link>
    </div>
  );
}

export default Question;
