import React, { useState } from "react";
import { fbChecklist } from "../data";

function Question({ id, category, text, checked }) {
  //get the array containing this check object
  //to be used to update it's checked property in handleChange() function
  const thisQuestion = fbChecklist.filter((question) => {
    return question.id === id;
  });
  const handleChange = () => {
    //update the property whenever checked is clicked
    thisQuestion[0].checked = !checked;
  };

  return (
    <div>
      <span>{text}</span>
      <input
        type="checkbox"
        id={id}
        name={`Check${id}`}
        onChange={() => {
          handleChange();
        }}
      />
      <FaRegEdit />
    </div>
  );
}

export default Question;
