import React, { useState, useContext, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
import Question from "../components/Question";
import { Context } from "../Context";
import Loading from "./Loading";
import Navbar from "../Navbar";
import { makeStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Checkbox from "@material-ui/core/Checkbox";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";

//styling for the fbchecklist page
const useStyles = makeStyles((theme) => ({
  root: {
    // width: "100%",
    // maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  button: {
    color: "#F15A22",
    fontWeight: "medium",
    backgroundColor: theme.palette.background.paper,
  },
}));

function FbChecklist() {
  //get tenant id from url
  const { tenantId } = useParams();

  const [fbChecklistState, setFbChecklistState] = useState();
  //Context: Fb Checklist
  const {
    getFbChecklistQuestions,
    createFbReportState,
    setFbReportState,
  } = useContext(Context);

  const classes = useStyles();

  const [checked, setChecked] = useState([]);

  const handleToggle = (question_id) => () => {
    //current question id
    const currentIndex = checked.indexOf(question_id);

    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(question_id);
    } else {
      newChecked.splice(currentIndex, 1);
    }
    //update the checked state
    setChecked(newChecked);
    //update fb report state
    setFbReportState((prevState) => {
      return prevState.map((question) =>
        question.qn_id === question_id
          ? { ...question, status: !question.status }
          : question
      );
    });
  };

  useEffect(() => {
    //function to retrieve questions
    getFbChecklistQuestions()
      .then((response) => {
        setFbChecklistState(response.data);
        createFbReportState(response.data);
      })
      .catch(() => {
        console.log("fb checklist retrieval failed");
      });
  }, []);

  return (
    <>
      {fbChecklistState ? (
        <>
          <Navbar />
          <Grid container spacing={4}>
            <Grid item xs={12} sm={6} md={4}>
              <List dense className={classes.root}>
                {fbChecklistState.map((question, index) => {
                  const { fb_qn_id, requirement } = question;
                  const labelId = `checkbox-list-secondary-label-${fb_qn_id}`;
                  return (
                    <ListItem key={index} button>
                      <ListItemText id={fb_qn_id} primary={`${requirement}`} />
                      <ListItemSecondaryAction>
                        <Checkbox
                          edge="end"
                          onChange={handleToggle(fb_qn_id)}
                          checked={checked.indexOf(fb_qn_id) !== -1}
                          inputProps={{ "aria-labelledby": labelId }}
                        />
                      </ListItemSecondaryAction>
                    </ListItem>
                  );
                })}
              </List>
              <Button
                className={classes.button}
                size="small"
                // color="secondary"
              >
                View Tenants
              </Button>
            </Grid>
          </Grid>
        </>
      ) : (
        <Loading />
      )}
    </>
    // <div>
    //   {fbChecklistState ? (
    //     <>
    //       <Navbar />
    //       <div className="category-head">
    //         <h2>FB Checklist</h2>
    //         {fbChecklistState.map((question, index) => {
    //           const { fb_qn_id, requirement } = question;
    //           return (
    //             <Question
    //               key={index}
    //               fb_qn_id={fb_qn_id}
    //               requirement={requirement}
    //               tenantId={tenantId}
    //             />
    //           );
    //         })}
    //         <br />
    //         {/* <Link to={`/tenant/${tenantId}`}> */}
    //         <button
    //           // onClick={() => {
    //           //   updateAudit(tenantId, "FB", tenantName, "unresolved");
    //           //   resetTenantFbChecklist(tenantId);
    //           // }}
    //           onClick={() => submitFbReport(1006, fbReportState)}
    //         >
    //           Submit
    //         </button>
    //         {/* </Link> */}
    //       </div>
    //     </>
    //   ) : (
    //     <Loading />
    //   )}
    // </div>
  );
}

export default FbChecklist;
