import React, { useState, useContext } from "react";
import ReactDOM from "react-dom";
import { Context } from "../Context";
import { fbChecklist } from "../data";
import { FaRegTimesCircle } from "react-icons/fa";

function Modal({ id, tenantId }) {
  //Context: state of modal (whether it is open or not)
  const { modalOpen, closeModal } = useContext(Context);
  //state for comments and updating comments
  const { comment, setComment, updateTenantComment } = useContext(Context);
  //state of displayedComments
  const { tenantsState } = useContext(Context);

  //handle change in textbox by updating comment state
  const handleChange = (event) => {
    setComment(event.target.value);
  };
  //handle submit
  const handleSubmit = (event) => {
    updateTenantComment(tenantId, id);
  };
  const tenantObject = tenantsState.find((tenant) => {
    return tenant.tenantid === tenantId;
  });

  return ReactDOM.createPortal(
    <>
      <section
        className={`${
          modalOpen ? "modal-overlay show-modal" : "modal-overlay"
        }`}
      >
        <div className="modal-container">
          <FaRegTimesCircle
            className="modal-closebtn"
            onClick={() => {
              closeModal();
            }}
          />
          <div>Write a Comment</div>
          <textarea
            className="modal-commentbox"
            value={comment}
            onChange={(event) => {
              handleChange(event);
            }}
          />
          <button
            onClick={() => {
              handleSubmit();
            }}
          >
            Submit
          </button>
        </div>
      </section>
    </>,
    document.getElementById("portal")
  );
}

export default Modal;
