import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Accordion from "@material-ui/core/Accordion";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import AccordionDetails from "@material-ui/core/AccordionDetails";
import Checkbox from "@material-ui/core/Checkbox";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import PhotoCamera from "@material-ui/icons/PhotoCamera";
import { Typography } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  commentBox: {
    maxWidth: 800,
    width: "100%",
    padding: theme.spacing(0, 0, 2, 2),
  },
  comment: {
    width: "100%",
  },
  dropdownMain: {
    display: "flex",
    flexDirection: "column",
  },
  dropdownContainer: {
    display: "flex",
    flexDirection: "column",
    margin: theme.spacing(0, 0, 0, 2),
  },
  textInfo: {
    padding: theme.spacing(2, 0, 2, 0),
    color: "#F15A22",
  },
  topText: {
    color: "#F15A22",
  },
  textTenant: {
    padding: theme.spacing(1, 0, 2, 2),
    fontWeight: "medium",
  },
  tenantInstructions: {
    display: "flex",
    padding: theme.spacing(2, 0, 2, 2),
  },
  tenantResponse: {
    display: "flex",
    width: "100%",
  },
  avatar: {},
  button: {
    width: "50%",
    color: "#F15A22",
    fontWeight: "medium",
    backgroundColor: theme.palette.background.default,
  },
  buttonContainer: {
    padding: theme.spacing(4, 0, 4, 0),
    display: "flex",
    justifyContent: "center",
  },
  input: {
    display: "none",
  },
  camera: {
    display: "flex",
    flexDirection: "column",
    //   alignItems: "center",
  },
  uploadText: {
    padding: theme.spacing(0, 1, 0, 2),
  },
}));

function TenantReportCard({ remarks, entry_id, requirement, timeframe }) {
  const classes = useStyles();
  const [comment, setComment] = useState("");
  const { fbReportState, setFbReportState } = useContext(Context);
  //function to update comment state
  const handleComment = (e) => {
    setComment(e.target.value);
    console.log(comment);
  };

  const handleSubmit = () => {};
  return (
    <Accordion>
      <AccordionSummary
        expandIcon={<ExpandMoreIcon />}
        aria-label="Expand"
        aria-controls="additional-actions1-content"
        id="additional-actions1-header"
      >
        <ListItem>
          <ListItemText id={entry_id} primary={requirement} />
        </ListItem>
      </AccordionSummary>
      <AccordionDetails className={classes.dropdownMain}>
        <div className={classes.dropdownContainer}>
          <Typography
            color="textSecondary"
            variant="button"
            className={classes.topText}
          >
            REMARKS: {remarks}
          </Typography>

          <Typography
            color="textSecondary"
            variant="button"
            className={classes.textInfo}
          >
            RECTIFICATION PERIOD: {timeframe}
          </Typography>
        </div>
        <div className={classes.tenantInstructions}>
          <Avatar src="/broken-image.jpg" className={classes.avatar} />
          <Typography color="textPrimary" className={classes.textTenant}>
            Please respond to non-compliance below:
          </Typography>
        </div>
        <div className={classes.tenantResponse}>
          <TextField
            id="standard-multiline-static"
            label="Comment on rectification to non-compliance"
            multiline
            value={comment}
            className={classes.comment}
            onChange={(e) => {
              handleComment(e);
            }}
          />

          <input
            accept="image/*"
            className={classes.input}
            id="icon-button-file"
            type="file"
          />
          <label htmlFor="icon-button-file" className={classes.camera}>
            <IconButton
              color="primary"
              aria-label="upload picture"
              component="span"
            >
              <Typography variant="button" className={classes.uploadText}>
                Upload photo
              </Typography>
              <PhotoCamera />
            </IconButton>
          </label>
        </div>
        <div className={classes.buttonContainer}>
          <Button
            className={classes.button}
            size="large"
            onClick={() => {
              handleSubmit(comment);
            }}
            // color="secondary"
          >
            submit
          </Button>
        </div>
      </AccordionDetails>
    </Accordion>
  );
}

export default TenantReportCard;
