import React, { useEffect, useContext, useState } from "react";
import { Link } from 'react-router-dom';
import { Typography, Button } from "@material-ui/core";
import axios from "axios";

import { Context } from "../Context";
import Navbar from "../Navbar";
import useStyles from "../styles";
import { FormGroup } from "@material-ui/core";
import ChatCards from "../components/ChatCards";
import ChatEntriesCards from "../components/ChatEntriesCard";

function Chat() {

  const {
    setAllChatsOfUserState,
    allChatsOfUserState,
    getAllChatsOfUser,
    setChatEntriesOfUserState,
    chatEntriesOfUserState,
    getChatEntriesOfUser,
  } = useContext(Context);

  const [allChatsState, setAllChatsState] = useState([]);
  const styles = useStyles();
  const chatsArray = [];
  const chatEntriesDict = {};

  const parentChatId = "2";
  const numLastestChatEntries = "1";

  useEffect(() => {
    //getAllChatsOfUser();
    async function getResponse() {
      try{
        await getAllChatsOfUser().then((response_parent) => {
          //console.log("allChatsOfUser: " + response_parent.data);
          response_parent.data.map((chat) => {
            //console.log(chat);
            let newChat = {};
            newChat.chat_id = chat.chat_id;
            newChat.tenant_id = chat.tenant_id;
            newChat.auditor_id = chat.auditor_id;
            if (chat.messages.messages) {
              newChat.messages = [...chat.messages.messages];
            } else {
              newChat.messages = ["No message"];
            };
            chatsArray.push(newChat);

            try {
              getChatEntriesOfUser(chat.chat_id.toString()).then((response_child) => {
                console.log("chatEntriesOfUser: " + response_child.data);
                response_child.data.map((entry) => {
                  chatEntriesDict[chat.chat_id] = entry;
                });
                setChatEntriesOfUserState(chatEntriesDict);
                console.log("chatEntriesOfUserDict: " + chatEntriesDict);
                console.log("chatEntriesOfUser: " + chatEntriesOfUserState);
              })
            } catch {
              console.log("Failed to retrive chatEntriesOfUser")
            }
          });
          setAllChatsOfUserState(chatsArray);
          console.log("allChatsOfUser: " + chatsArray);
        });
      } catch {
        console.log("Failed to retrive allChatsOfUser");
      }
    }
    getResponse();
  }, []);

  return (
    <main className={styles.main}>
      <Navbar />
      <br />
      <Typography variant="h3" align="center">Chat</Typography>
      <ChatCards />
      <br />
      <br />
    </main>
  )
}

export default Chat;
