import React, { useState, useContext } from "react";
import { fbChecklist } from "../data";
import Modal from "./Modal";
import { FaRegEdit } from "react-icons/fa";
import { Context } from "../Context";

function Question({ id, text }) {
  //Context: state of modal (whether it is open or not)
  const { setModalOpen, closeModal } = useContext(Context);
  //Context: Fb Checklist
  const { fbChecklistState } = useContext(Context);
  //get the array containing this question object
  //to be used to update it's checked property in handleChange() function
  const thisQuestion = fbChecklistState.find((question) => {
    return question.id === id;
  });

  return (
    <div>
      <span>{text}</span>
      <input
        type="checkbox"
        id={id}
        name={`Check${id}`}
        // onChange={() => {
        //   handleChange();
        // }
      />
      <FaRegEdit
        onClick={() => {
          setModalOpen(true);
        }}
      />
      <Modal id={id} />
    </div>
  );
}

export default Question;
