import React, { useEffect, useContext } from "react";
import { Typography } from "@material-ui/core";
import axios from "axios";

import { Context } from "../Context";
import Navbar from "../Navbar";
import useStyles from "../styles";

function Account() {
  const {
    accountState,
    getAccountInfo
  } = useContext(Context);

  // All possible account info categories for 3 types of users
  const {
    acc_id,
    branch_id,
    employee_id,
    email,
    username,
    first_name,
    last_name,
    hp,
    mgr_id,
    role_id,
    store_id,
    type_id,
  } = accountState;

  useEffect(() => {
    getAccountInfo();
  }, []);

  const styles = useStyles();
  const API_URL = "http://localhost:8080";

  // Used to generate one category of account info
  // Filter out those empty categories
  const AccountInfo = (props) => {
    if (props.info != null) {
      return <Typography align="center">{props.category}: {props.info}</Typography>;
    } else {
      return null;
    };
  };

  function postAccountChange(newUsername, newFirstName, newLastName, newEmail, newHp) {
    console.log("This is posting account changes");
    let payload = {
      username: newUsername,
      first_name: newFirstName,
      last_name: newLastName,
      email: newEmail,
      hp: newHp,
    };
    let FormData = require("form-data");
    let formdata = new FormData();
    formdata.append("changes", JSON.stringify(payload));
    return axios
      .post(
        `${API_URL}/account/postProfileUpdate`,
        formdata,
        {
          headers: {
            "Content-Type": `multipart/form-data; boundary=${formdata._boundary}`,
          },
        }
      )
      .then((response) => {
        console.log(response);
      })
      .catch(() => {
        console.log("Failed account change submission");
      });
  }

  function postPasswordChange(newPassword) {
    console.log("This is posting password change");
    let FormData = require("form-data");
    let formdata = new FormData();
    formdata.append("new_password", JSON.stringify(newPassword));
    return axios
      .post(
        `${API_URL}/account/postPasswordUpdate`,
        formdata,
        {
          headers: {
            "Content-Type": `multipart/form-data; boundary=${formdata._boundary}`,
          },
        }
      )
      .then((response) => {
        console.log(response);
      })
      .catch(() => {
        console.log("Failed password change submission");
      })
  }

  const myNewUsername = "hannz"; // Once username changes, need to redirect to the login page
  const myNewFirstName = "Hannah";
  const myNewLastName = "Hannah";
  const myNewEmail = "hannahHAHAHA@gmail.com";
  const myNewHp = 12345678;

  const myNewPassword = "test1234";

  return (
    <main className={styles.main}>
      <Navbar />
      <br />
      <Typography variant="h3" align='center'>Account</Typography>
      <br />
      <AccountInfo category="Username" info={username}/>
      <AccountInfo category="First Name" info={first_name}/>
      <AccountInfo category="Last Name" info={last_name}/>
      <br />
      <AccountInfo category="Email" info={email}/> 
      <AccountInfo category="Contact Number" info={hp}/>
      <AccountInfo category="Role" info={role_id}/>
      <br />
      <AccountInfo category="Account ID" info={acc_id}/>
      <AccountInfo category="Branch ID" info={branch_id}/>
      <AccountInfo category="Employee ID" info={employee_id} />
      <AccountInfo category="Mgr ID" info={mgr_id}/>
      <AccountInfo category="Store ID" info={store_id}/>
      <AccountInfo category="Store Type" info={type_id}/>
      <br />

      <button onClick={() => postAccountChange(
        myNewUsername,
        myNewFirstName,
        myNewLastName,
        myNewEmail,
        myNewHp
      )}>Change Account Info</button>

      <button onClick={() => postPasswordChange(
        myNewPassword
      )}>Change My Password</button>

    </main>
  );
}

export default Account;
