import React, { useState, useContext, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
import Question from "../components/Question";
import { Context } from "../Context";
import Loading from "./Loading";
import Navbar from "../Navbar";
import { makeStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Checkbox from "@material-ui/core/Checkbox";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import AccountCircle from "@material-ui/icons/AccountCircle";
import InputAdornment from "@material-ui/core/InputAdornment";
import Accordion from "@material-ui/core/Accordion";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import AccordionDetails from "@material-ui/core/AccordionDetails";

import FormControlLabel from "@material-ui/core/FormControlLabel";
import Typography from "@material-ui/core/Typography";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";

//styling for the fbchecklist page
const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: theme.spacing(5, 0, 5, 0),
  },
  list: {
    width: "100%",
    maxWidth: 800,
    backgroundColor: theme.palette.background.paper,
    display: "flex",
    flexDirection: "column",

    // alignItems: "center",
  },
  button: {
    width: "100%",
    height: 50,
    maxWidth: 800,
    color: "#F15A22",
    fontWeight: "medium",
    backgroundColor: theme.palette.background.paper,
  },
  listItem: {
    padding: theme.spacing(2, 5, 2, 5),
  },
  checkbox: {
    // padding: theme.spacing(0, 2, 0, 3),
  },
  commentBox: {
    padding: theme.spacing(2, 5, 2, 5),
  },
  dialog: {
    backgroundColor: "transparent",
  },
}));

function FbChecklist() {
  //use styles from function declared above
  const classes = useStyles();
  //get tenant id from url
  const { tenantId } = useParams();

  //Context
  const {
    getFbChecklistQuestions,
    createFbReportState,
    setFbReportState,
  } = useContext(Context);

  //state to update fb checklist questions
  const [fbChecklistState, setFbChecklistState] = useState();
  //state to update number of checked boxes
  const [checked, setChecked] = useState([]);
  //state to update comments
  const [comments, setComments] = useState([]);
  //state for dialog modal
  const [open, setOpen] = useState(false);

  useEffect(() => {
    //function to retrieve questions
    getFbChecklistQuestions()
      .then((response) => {
        setFbChecklistState(response.data);
        createFbReportState(response.data);
      })
      .catch(() => {
        console.log("fb checklist retrieval failed");
      });
  }, []);

  //handle the checkbox changes
  const handleToggle = (question_id) => () => {
    //current question id
    const currentIndex = checked.indexOf(question_id);

    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(question_id);
    } else {
      newChecked.splice(currentIndex, 1);
    }
    //update the checked state
    setChecked(newChecked);
    //update fb report state
    setFbReportState((prevState) => {
      return prevState.map((question) =>
        question.qn_id === question_id
          ? { ...question, status: !question.status }
          : question
      );
    });
  };

  return (
    <>
      {fbChecklistState ? (
        <>
          <Navbar />
          <Grid container className={classes.root}>
            <List dense className={classes.list}>
              {fbChecklistState.map((question, index) => {
                const { fb_qn_id, requirement } = question;
                const labelId = `checkbox-list-secondary-label-${fb_qn_id}`;
                return (
                  <>
                    <Accordion>
                      <AccordionSummary
                        expandIcon={<ExpandMoreIcon />}
                        aria-label="Expand"
                        aria-controls="additional-actions1-content"
                        id="additional-actions1-header"
                      >
                        <ListItem key={index} button>
                          <Checkbox
                            // edge="end"
                            onChange={handleToggle(fb_qn_id)}
                            checked={checked.indexOf(fb_qn_id) !== -1}
                            inputProps={{ "aria-labelledby": labelId }}
                          />

                          <ListItemText
                            id={fb_qn_id}
                            primary={`${requirement}`}
                          />
                        </ListItem>
                      </AccordionSummary>
                      <AccordionDetails>
                        <Typography color="textSecondary">
                          The click event of the nested action will propagate up
                          and expand the accordion unless you explicitly stop
                          it.
                        </Typography>
                      </AccordionDetails>
                    </Accordion>

                    {/* <TextField
                      className={classes.commentBox}
                      id="input-with-icon-textfield"
                      label="Comment"
                      InputProps={{
                        startAdornment: (
                          <InputAdornment position="start">
                            <AccountCircle />
                          </InputAdornment>
                        ),
                      }}
                    /> */}
                  </>
                );
              })}
            </List>
            <Button
              className={classes.button}
              size="small"
              // color="secondary"
            >
              Submit
            </Button>
          </Grid>
        </>
      ) : (
        <Loading />
      )}
    </>
    // <div>
    //   {fbChecklistState ? (
    //     <>
    //       <Navbar />
    //       <div className="category-head">
    //         <h2>FB Checklist</h2>
    //         {fbChecklistState.map((question, index) => {
    //           const { fb_qn_id, requirement } = question;
    //           return (
    //             <Question
    //               key={index}
    //               fb_qn_id={fb_qn_id}
    //               requirement={requirement}
    //               tenantId={tenantId}
    //             />
    //           );
    //         })}
    //         <br />
    //         {/* <Link to={`/tenant/${tenantId}`}> */}
    //         <button
    //           // onClick={() => {
    //           //   updateAudit(tenantId, "FB", tenantName, "unresolved");
    //           //   resetTenantFbChecklist(tenantId);
    //           // }}
    //           onClick={() => submitFbReport(1006, fbReportState)}
    //         >
    //           Submit
    //         </button>
    //         {/* </Link> */}
    //       </div>
    //     </>
    //   ) : (
    //     <Loading />
    //   )}
    // </div>
  );
}

export default FbChecklist;
