import React, { useEffect, useContext } from "react";
import { Typography, Button, Input, TextField, FormGroup, FormControlLabel } from "@material-ui/core";
import axios from "axios";

import { Context } from "../Context";
import Navbar from "../Navbar";
import useStyles from "../styles";
import { Container } from "@material-ui/core";

function EditAccount() {

  const {
    accountState,
    getAccountInfo
  } = useContext(Context);

  // All possible account info categories for 3 types of users
  const {
    acc_id,
    branch_id,
    employee_id,
    email,
    username,
    first_name,
    last_name,
    hp,
    mgr_id,
    role_id,
    store_id,
    type_id,
  } = accountState;

  useEffect(() => {
    getAccountInfo();
  }, []);

  const API_URL = "http://localhost:8080";
  const styles = useStyles();
  const disabledInfo = [
    "Role", 
    "Account ID", 
    "Employee ID", 
    "Branch ID", 
    "Mgr ID", 
    "Store ID", 
    "Store Type"
  ];

  const EditAccountInfo = (props) => {
    if (props.info == null) {
      return null
    } else if (disabledInfo.includes(props.category)) {
      return <TextField label={props.category} defaultValue={props.info} disabled="true"/>
    } else {
      return <TextField label={props.category} defaultValue={props.info} />
    }
  }

  function postAccountChange(newUsername, newFirstName, newLastName, newEmail, newHp) {
    console.log("This is posting account changes");
    let payload = {
      username: newUsername,
      first_name: newFirstName,
      last_name: newLastName,
      email: newEmail,
      hp: newHp,
    };
    let FormData = require("form-data");
    let formdata = new FormData();
    formdata.append("changes", JSON.stringify(payload));
    return axios
      .post(
        `${API_URL}/account/postProfileUpdate`,
        formdata,
        {
          headers: {
            "Content-Type": `multipart/form-data; boundary=${formdata._boundary}`,
          },
        }
      )
      .then((response) => {
        console.log(response);
      })
      .catch(() => {
        console.log("Failed account change submission");
      });
  }

  return (
    <main className={styles.main}>
      <Typography variant="h3" align="center">Edit Account</Typography>
      <br />
      <FormGroup column="true">
        <EditAccountInfo category="Username" info={username}/>
        <EditAccountInfo category="First Name" info={first_name}/>
        <EditAccountInfo category="Last Name" info={last_name}/>
        <br />
        <EditAccountInfo category="Email" info={email}/>
        <EditAccountInfo category="Contact Number" info={hp}/>
        <EditAccountInfo category="Role" info={role_id}/>
        <br />
        <EditAccountInfo category="Account ID" info={acc_id}/>
        <EditAccountInfo category="Branch ID" info={branch_id}/>
        <EditAccountInfo category="Employee ID" info={employee_id} />
        <EditAccountInfo category="Mgr ID" info={mgr_id}/>
        <EditAccountInfo category="Store ID" info={store_id}/>
        <EditAccountInfo category="Store Type" info={type_id}/>
        <br />
      </FormGroup>
      <Button
        className={styles.buttons}
        align="center"
        variant="outlined"
        color="primary"
      >
        Submit
      </Button>
    </main>
  )
}

export default EditAccount;
