import React, { useEffect, useState, useContext } from "react";
import { useParams } from "react-router-dom";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import Navbar from "../Navbar";
import { makeStyles } from "@material-ui/core/styles";
import { Context } from "../Context";
import Loading from "./Loading";
import Accordion from "@material-ui/core/Accordion";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import AccordionDetails from "@material-ui/core/AccordionDetails";
import Checkbox from "@material-ui/core/Checkbox";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import Avatar from "@material-ui/core/Avatar";
import TenantReportComment from "../components/TenantReportComment";
import TenantReportFileUpload from "../components/TenantReportFileUpload";

const useStyles = makeStyles((theme) => ({
  header: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(2, 0, 2, 0),
  },
  root: {
    width: "100%",
    padding: theme.spacing(4, 0, 10, 0),
  },
  dropdownMain: {
    display: "flex",
    flexDirection: "column",
  },
  dropdownContainer: {
    display: "flex",
    flexDirection: "column",
    margin: theme.spacing(0, 0, 0, 2),
  },
  textInfo: {
    padding: theme.spacing(2, 0, 2, 0),
    color: "#F15A22",
  },
  topText: {
    color: "#F15A22",
  },
  textTenant: {
    padding: theme.spacing(1, 0, 2, 2),
    fontWeight: "medium",
  },
  tenantInstructions: {
    display: "flex",
    padding: theme.spacing(2, 0, 2, 2),
  },
  tenantResponse: {
    display: "flex",
    width: "100%",
    border: "1px solid",
  },
  avatar: {},
  // comment: {
  //   width: "100%",
  // },
}));

function TenantReport() {
  const { reportId } = useParams();
  const { getReportStats, getReportEntry } = useContext(Context);
  const classes = useStyles();
  const [failedEntries, setFailedEntries] = useState();

  useEffect(() => {
    async function getResponse() {
      try {
        const entryArray = await getReportStats(reportId).then((response) => {
          console.log(response);
          return response.data.Failed_Entries;
        });
        // console.log(entryArray);

        let entryInfoArray = [];

        for (let i = 0; i < entryArray.length; i++) {
          // console.log(entryArray[i]);
          // console.log(reportId);
          let info = await getReportEntry(reportId, entryArray[i]).then(
            (response) => {
              //   console.log(response);
              return response;
            }
          );
          entryInfoArray.push(info);
        }
        if (entryInfoArray.length === entryArray.length) {
          console.log(entryInfoArray);
          setFailedEntries(entryInfoArray);
        }
      } catch (err) {
        console.error(err);
      }
    }
    getResponse();
  }, []);

  return (
    <div>
      {failedEntries ? (
        <>
          <Navbar />
          <Box className={classes.header} textAlign="center" boxShadow={1}>
            <Typography variant="h5">Report Non-Compliance Overview</Typography>
          </Box>
          <Box className={classes.reportBasicInfo}></Box>
          <div className={classes.root}>
            {failedEntries.map((entry) => {
              const { data } = entry;
              let severity = data.severity / 1000000;
              severity = Math.floor(severity);
              let timeframe = "";
              switch (severity) {
                case 1:
                  timeframe = "3 weeks";
                  break;
                case 2:
                  timeframe = "2 weeks";
                  break;
                case 3:
                  timeframe = "1 week";
                  break;
                case 4:
                  timeframe = "4 days";
                  break;
                case 5:
                  timeframe = "1 day";
                  break;
              }
              return (
                <Accordion>
                  <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                    aria-label="Expand"
                    aria-controls="additional-actions1-content"
                    id="additional-actions1-header"
                  >
                    <ListItem>
                      <ListItemText
                        id={data.entry_id}
                        primary={data.Requirement}
                      />
                    </ListItem>
                  </AccordionSummary>
                  <AccordionDetails className={classes.dropdownMain}>
                    <div className={classes.dropdownContainer}>
                      <Typography
                        color="textSecondary"
                        variant="button"
                        className={classes.topText}
                      >
                        REMARKS: {data.remarks}
                      </Typography>

                      <Typography
                        color="textSecondary"
                        variant="button"
                        className={classes.textInfo}
                      >
                        RECTIFICATION PERIOD: {timeframe}
                      </Typography>
                    </div>
                    <div className={classes.tenantInstructions}>
                      <Avatar
                        src="/broken-image.jpg"
                        className={classes.avatar}
                      />
                      <Typography
                        color="textPrimary"
                        className={classes.textTenant}
                      >
                        Please respond to non-compliance below:
                      </Typography>
                    </div>
                    <Box className={classes.tenantResponse}>
                      <TenantReportComment className={classes.comment} />
                      <TenantReportFileUpload />
                    </Box>
                  </AccordionDetails>
                </Accordion>
              );
            })}
          </div>
        </>
      ) : (
        <Loading />
      )}
    </div>
  );
}

export default TenantReport;
