import React, { useState, useContext, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { Context } from "../Context";

function Tenant() {
  //get tenantid from url
  const { tenantId } = useParams();

  //Context: tenants array
  const { tenantsState } = useContext(Context);

  const tenantObject = tenantsState.find(
    (tenant) => tenant.tenantid === tenantId
  );

  const {
    tenantid,
    tenantName,
    timeRemaining,
    status,
    institution,
    latestScore,
  } = tenantObject;

  useEffect(() => {
    let score = tenantObject.fbChecklist.reduce((total, question) => {
      const { checked } = question;
      if (checked === true) {
        total++;
      }
      return total;
    });
  }, [tenantsState]);

  return (
    <div className="tenant-container">
      <section className="tenant-header">
        <div>hello {tenantName}</div>
      </section>
      <section className="tenant-content">{latestScore}</section>
      <Link to={`/tenant/fbChecklist/${tenantid}`}>Conduct Audit</Link>
    </div>
  );
}

export default Tenant;
