import React, { useState, useContext, useEffect } from "react";
import TextField from "@material-ui/core/TextField";
import { makeStyles } from "@material-ui/core/styles";
import { Context } from "../Context";
import Accordion from "@material-ui/core/Accordion";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import AccordionDetails from "@material-ui/core/AccordionDetails";

import ExpandMoreIcon from "@material-ui/icons/ExpandMore";

import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import PhotoCamera from "@material-ui/icons/PhotoCamera";
import { Typography } from "@material-ui/core";
import { useForm } from "react-hook-form";

const useStyles = makeStyles((theme) => ({
  commentBox: {
    maxWidth: 800,
    width: "100%",
    padding: theme.spacing(0, 0, 2, 2),
  },
  comment: {
    width: "100%",
  },
  dropdownMain: {
    display: "flex",
    flexDirection: "column",
  },
  dropdownContainer: {
    display: "flex",
    flexDirection: "column",
    margin: theme.spacing(0, 0, 0, 2),
  },
  textInfo: {
    padding: theme.spacing(2, 0, 2, 0),
    color: "#F15A22",
  },
  topText: {
    color: "#F15A22",
  },
  textTenant: {
    padding: theme.spacing(1, 0, 2, 2),
    fontWeight: "medium",
  },
  tenantInstructions: {
    display: "flex",
    padding: theme.spacing(2, 0, 2, 2),
  },
  tenantResponse: {
    display: "flex",
    width: "100%",
  },
  avatar: {},
  button: {
    width: "50%",
    color: "#F15A22",
    fontWeight: "medium",
    backgroundColor: theme.palette.background.default,
  },
  buttonContainer: {
    padding: theme.spacing(4, 0, 4, 0),
    display: "flex",
    justifyContent: "center",
  },
  input: {
    display: "none",
  },
  camera: {
    display: "flex",
    flexDirection: "column",
    //   alignItems: "center",
  },
  uploadText: {
    padding: theme.spacing(0, 1, 0, 2),
  },
}));

function TenantReportCard({
  remarks,
  entry_id,
  requirement,
  timeframe,
  report_id,
}) {
  const classes = useStyles();
  const [comment, setComment] = useState("");
  //selected file state
  // const [selectedFile, setSelectedFile] = useState();
  const [base64, setBase64] = useState();
  const [testState, setTestState] = useState("hi");
  //state to check if file is selected
  // const [isFilePicked, setIsFilePicked] = useState(false);

  const {
    fbReportState,
    setFbReportState,
    getReportEntry,
    submitReportUpdate,
  } = useContext(Context);

  useEffect(() => {
    console.log("Test" + testState);
    console.log(testState);
  }, [testState]);

  //function to update comment state
  const handleComment = (e) => {
    setComment(e.target.value);
    console.log(testState);
  };
  // handle input file change
  const handleChange = (e) => {
    // console.log(selectedFile);
    // console.log(event.target.files[0]);
    // if (event.target.files[0] !== null) {
    //   setSelectedFile(event.target.files[0]);
    // }
    setTestStateFunction();
    const getBase64 = (file) => {
      return new Promise((resolve, reject) => {
        const reader = new FileReader();

        if (file) {
          reader.readAsDataURL(file);
        }

        reader.onload = () => resolve(reader.result);
        reader.onerror = (error) => reject(error);
      });
    };
    getBase64(e.target.files[0]).then((image) => {
      console.log("im here at the base64 method");
      setBase64(image);

      // console.log(image);
    });
    // console.log("going to set the test state now");
    // setTestState("hello");
    // console.log(testState);
    // setIsFilePicked(true);
  };
  const setTestStateFunction = () => {
    console.log("entered function");
    setTestState("hello");
  };
  // let input = document.getElementsByTagName("input")[0];

  // input.onclick = function () {
  //   this.value = null;
  // };

  // input.onchange = (event) => {
  //   setSelectedFile(event.target.files[0]);
  //   console.log(event.target.files[0]);
  //   setIsFilePicked(true);
  // };

  //handle submit
  const handleSubmit = () => {
    // console.log(testState);

    async function getEntry() {
      const entry = await getReportEntry(report_id, entry_id).then(
        (response) => {
          console.log(entry_id);
          console.log(response);
          return response.data;
        }
      );

      // if (base64) {
      //   console.log(base64);
      //   submitReportUpdate(report_id, false, "", {
      //     ...entry,
      //     images: [base64],
      //     remarks: comment,
      //   });
      // }

      // console.log(selectedFile);

      // getBase64().then((image) => {
      //   console.log(image);
      // });

      // getBase64().then((image) => {
      //   console.log(image);
      //   console.log(comment);
      //   submitReportUpdate(report_id, false, "", {
      //     ...entry,
      //     images: [image],
      //     remarks: comment,
      //   });
      // });

      alert("Rectification submitted. Pending Approval");
    }
    getEntry();

    console.log(testState);
  };

  return (
    <Accordion>
      <AccordionSummary
        expandIcon={<ExpandMoreIcon />}
        aria-label="Expand"
        aria-controls="additional-actions1-content"
        id="additional-actions1-header"
      >
        <ListItem>
          <ListItemText id={entry_id} primary={requirement} />
        </ListItem>
      </AccordionSummary>
      <AccordionDetails className={classes.dropdownMain}>
        <div className={classes.dropdownContainer}>
          <Typography
            color="textSecondary"
            variant="button"
            className={classes.topText}
          >
            REMARKS: {remarks}
          </Typography>

          <Typography
            color="textSecondary"
            variant="button"
            className={classes.textInfo}
          >
            RECTIFICATION PERIOD: {timeframe}
          </Typography>
        </div>
        <div className={classes.tenantInstructions}>
          <Avatar src="/broken-image.jpg" className={classes.avatar} />
          <Typography color="textPrimary" className={classes.textTenant}>
            Please respond to non-compliance below:
          </Typography>
        </div>
        <div className={classes.tenantResponse}>
          <TextField
            id="standard-multiline-static"
            label="Comment on rectification to non-compliance"
            multiline
            value={comment}
            className={classes.comment}
            onChange={(e) => {
              handleComment(e);
            }}
          />
          <input
            // accept="image/*"
            className={classes.input}
            // id={`icon-button-file${entry_id}`}
            id="icon-button-file"
            name="file"
            type="file"
            // value={null}
            // name="picture"
            onClick={(e) => {
              e.target.value = "";
            }}
            onChange={(e) => {
              console.log("entered onChange function");
              setTestState("Hello");
              handleChange(e);
            }}
          />
          <label
            // htmlFor={`icon-button-file${entry_id}`}
            htmlFor="icon-button-file"
            className={classes.camera}
          >
            <IconButton
              color="primary"
              aria-label="upload picture"
              component="span"
            >
              <Typography variant="button" className={classes.uploadText}>
                Upload photo
              </Typography>
              <PhotoCamera />
            </IconButton>
          </label>
        </div>
        <div className={classes.buttonContainer}>
          <Button
            className={classes.button}
            size="large"
            onClick={() => {
              handleSubmit();
            }}
            // color="secondary"
          >
            submit
          </Button>
        </div>
      </AccordionDetails>
    </Accordion>
  );
}

export default TenantReportCard;
